package com.nguyenDat.PaneHome;import com.nguyenDat.DataDictionary.DataDictionarySQL;import com.nguyenDat.Rencent.RecentWord;import com.nguyenDat.Sound.SoundApp;import javax.swing.*;import javax.swing.event.ListSelectionEvent;import javax.swing.event.ListSelectionListener;import java.awt.*;public class ListWord implements ListSelectionListener {    private static JList list = new JList();    private JScrollPane Scroll = new JScrollPane(list);    public static boolean CapNhat = true;    private static String listWord[];    public ListWord() {        list.setBounds(25, 135, 255, 490);        list.setFont(new Font(Font.DIALOG, Font.BOLD, 14));        Scroll.setBounds(25, 135, 255, 490);        list.addListSelectionListener(this);        list.setSelectionBackground(new Color(0x1297EE));        Scroll.setBorder(BorderFactory.createLineBorder(new Color(0x07658F), 3));        PaneHome.getPaneHome().add(Scroll);    }    private static void copyList(String[] lis){        listWord = new String[201];        for(int i = 0; i < lis.length ; i++){            listWord[i] = lis[i];        }    }    public static void UpdateList() {        String w = InputH.getTextInput().getText();        if (w.equals("")) {            if(CapNhat) {                if (PaneHome.AnhViet) {                    copyList(RecentWord.getWordE());                }                else {                    copyList(RecentWord.getWordV());                }            }            CapNhat = false;        } else {            listWord = DataDictionarySQL.ListWord(w);        }        list.setListData(listWord);    }    @Override    public void valueChanged(ListSelectionEvent e) {        if (list.getSelectedIndex() != -1) {            SoundApp.setWord((String) list.getSelectedValue());            OutputH.getOutputApp().setText(DataDictionarySQL.SearchAnhViet((String) list.getSelectedValue()));            RecentWord.UpdateRecentWord((String) list.getSelectedValue(),PaneHome.AnhViet);        }    }}